      equiv: {
        "http-equiv": "Content-Type",
        content: "text/html; charset=UTF-8"
      }
    }
  },

  mounted() {
    // Deferred footer layout
    this.showFooter = true;
  },

  data() {
    return {
      showFooter: false,
      leftDrawerOpen: false
    };
  }

});

