.footer {
  font-size: 16px;
}
.footer-content {
  padding: 40px 70px;
}
.footer-inner {
  --gap-x: 20px;
  --gap-y: 40px;
  display: flex;
  margin: 20px 60px 110px;

  & > div {
    flex: 0 1 380px;
    margin: 0 var(--gap-x) var(--gap-y) 0;
  }
  .about {
    font-size: 16px;
    padding-right: 20px;
  }
  .links,
  .sns {
    flex-basis: 200px;
  }
  .sns-buttons {
    margin-left: -16px;
  }
  .links li {
    margin-bottom: 16px;
  }
}
.footer-copyright {
  margin: 10px 60px;
  letter-spacing: 2.6px;
  color: $primary;
}

@media (min-width: 1130px) {
  .footer-inner {
    .sns {
      margin-left: auto;
    }
  }
}
