import HelperMixin from "../../mixins/helpers";
import Navigation from "../../components/Navigation";
import { mapGetters, mapActions } from "vuex";

export default {
  name: "Profile",
  mixins: [HelperMixin],
  components: { Navigation },

  preFetch({ store, redirect }) {
    if (!store.getters["auth/isAuthenticated"]) {
      redirect("/account");
    }
  },

  meta() {
    return {
      title: "My Profile"
    };
  },

  computed: {
    user() {
      return this.$store.state.auth.user;
    }
  },
  created() {
    if (process.env.CLIENT) {
      this.getProfile();
      this.getOrders();
    }
  },
  mounted() {},
  data() {
    return {
      loading: false,
      fetching: true,
      fetchingOrders: true,
      profile: {},
      orders: []
    };
  },
  methods: {
    ...mapActions("auth", [
      "fetchProfile",
      "fetchOrders",
      "signout",
      "sendCode",
      "sendSMS"
    ]),
    async getProfile() {
      try {
        const resp = await this.fetchProfile();
        if (resp) {
          this.profile = { ...resp };
        }
      } catch (err) {
        this.$router.push("/account").catch(err => {});
      } finally {
        this.fetching = false;
      }
    },

    async getOrders() {
      try {
        const resp = await this.fetchOrders();
        if (resp) {
          this.orders = resp.slice();
        }
      } catch (err) {
        this.showNotif("negative", err);
      } finally {
        this.fetchingOrders = false;
      }
    },

    async resendCode() {
      try {
        this.loading = true;
        await this.sendCode();
        this.showNotif("info", "Verification code is sent to your email.");
        this.$router.push("/profile/verify").catch(err => {});
      } catch (err) {
        this.showNotif("negative", err);
      } finally {
        this.loading = false;
      }
    },

    async verifySMS() {
      try {
        await this.sendSMS();
        this.showNotif("info", "Verification code is sent to your phone.");
        this.$router.push("/profile/verifySMS").catch(err => {});
      } catch (err) {
        this.showNotif("negative", err);
      }
    },

    async onLogout() {
      try {
        await this.signout();
      } catch (err) {
      } finally {
        this.$router.push("/account").catch(err => {});
      }
    }
  }
};

