.mainpage > div {
  width: 100%;
}
.profile {
  min-height: 720px;
  font-size: 16px;
  max-width: 1420px;
  padding: 0 80px;
  margin: 60px auto;
  margin-bottom: 80px;

  & > div {
    width: 100%;
  }
  .logout {
    display: inline-block;
  }
  .profile-heading {
    margin-bottom: 80px;
  }
}

.profile-details {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  gap: 32px;

  .section {
    .section-label {
      line-height: 2.5em;
      width: 100%;
      border-bottom: 1px solid $line;
      margin: 0 0 40px;
    }
  }

  .orders {
    flex: 1 1 680px;

    .order-details {
      line-height: 1.65em;

      .order-detail {
        justify-content: space-between;
        border-bottom: 1px solid $line;
      }
    }
  }

  .details {
    flex-basis: 320px;
    .text-details {
      line-height: 1.65em;
    }
  }
}
