import HelperMixin from "../../mixins/helpers";
import Navigation from "../../components/Navigation";

export default {
  name: "Order",
  mixins: [HelperMixin],
  components: { Navigation },

  meta() {
    return {
      title: "Order Details"
    };
  },

  computed: {},
  created() {
    if (process.env.CLIENT) {
      this.getOrderDetails();
    }
  },
  mounted() {},
  data() {
    return {
      fetchErr: "",
      loading: false,
      fetching: true,
      order: {}
    };
  },
  methods: {
    _selectFirstImg(imgs) {
      if (!this.isEmpty(imgs)) {
        const retImg = imgs[0];
        if (retImg) return retImg.image;
      }
      return false;
    },

    async onCancel() {
      try {
        this.loading = true;
        await this.$store.dispatch("auth/cancelOrder", {
          orderID: this.$route.params.orderID
        });
        this.showNotif("info", "Your order has been cancelled.");
        this.$router.push("/profile").catch(err => {});
      } catch (err) {
        this.showNotif("negative", err);
      } finally {
        this.loading = false;
      }
    },

    async getOrderDetails() {
      try {
        const resp = await this.$store.dispatch("auth/fetchOrderDetails", {
          orderID: this.$route.params.orderID
        });
        if (resp) {
          this.order = JSON.parse(JSON.stringify(resp));
          this.fetching = false;
        } else {
          this.showNotif("warning", "No response received.");
        }
      } catch (err) {
        this.fetchErr = err;
        this.showNotif(
          "warning",
          "An error has occurred. Could not retrieve order details."
        );
        //this.$router.push("/profile").catch(err => {});
      }
    }
  }
};

