.sticky {
  position: -webkit-sticky;
  position: sticky;
  top: 20px;
}

.mainpage > div {
  width: 100%;
}
.navigation {
  position: absolute;
  z-index: 999;
  height: 106px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  color: $link;
  transition: background 0.5s ease-out, color 0.25s ease-out;
}
.navigation div {
  flex: 0 1 auto;
}
.left-nav {
  margin-left: 24px;
}
.right-nav {
  margin-right: 24px;
}
.right-nav li,
.left-nav li {
  font-size: 14px;
  margin: 0 14px;
  display: inline-block;
  flex-grow: 0;
  flex-basis: 240px;
}
.mobile {
  display: none;
}
.opaque-nav {
  background: $navbg;
  color: $dark;
}
.search-results {
  font-size: 16px;

  .section-label {
    line-height: 2.5em;
    width: 100%;
    border-bottom: 1px solid $line;
    margin: 0 0 40px;
  }
}
.header-banner {
  width: 100%;
  height: 550px;
  justify-content: center;
  align-items: center;

  .img-wrapper {
    background: $dark;
    transform: translate3d(0px, 0px, 0px);
    height: 100%;
    width: 100%;
    /* Create the parallax scrolling effect */
    background-attachment: fixed;
    background-position: center center;
    background-repeat: no-repeat;
    background-size: cover;
    filter: contrast(0.4);
    -webkit-filter: contrast(0.4);
  }
  .caption {
    position: absolute;
    margin: 0 auto;
  }
}
.content-main {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  margin-bottom: 60px;

  .nav-toolbar {
    width: 100%;
    height: 60px;
    margin-bottom: 20px;
    border-top: 1px solid $line;
    border-bottom: 1px solid $line;

    .nav-btn {
      display: flex;
      height: 100%;
      flex-basis: 160px;
      border-left: 1px solid $line;
    }
  }

  .products {
    display: flex;
    font-size: 16px;
    width: 100%;
    padding: 20px;

    .filter {
      padding-left: 20px;
      width: 180px;

      li {
        margin-bottom: 16px;
        font-size: 15px;
        color: $grey-8;
      }
    }
  }
}

// GALLERY
.product-list {
  width: 100%;
  display: grid;
  grid-gap: 40px;
  grid-template-columns: repeat(4, minmax(240px, 1fr));

  .product-item {
    .product-img {
      width: 100%;
      max-width: 100%;
      transition: transform 0.25s ease-out;
    }

    .product-img:hover {
      transform: translate(2px, 2px);
    }
  }
}

// SEARCH RESULTS
.sproduct-list {
  width: 100%;
  display: grid;
  grid-gap: 40px;
  justify-content: space-evenly;
  grid-template-columns: repeat(4, minmax(110px, 380px));

  .product-item {
    .product-img {
      width: 100%;
      max-width: 100%;
      transition: transform 0.25s ease-out;
    }

    .product-img:hover {
      transform: translate(2px, 2px);
    }
    .product-title {
      overflow: hidden;
    }
  }
}

.filter-drawer {
  .filter-title {
    display: flex;
    align-items: center;
    justify-content: center;
    border-bottom: 1px solid $line;

    & > i {
      position: absolute;
      right: 20px;
    }
  }

  .filter-selection {
    display: flex;
    font-size: 16px;
    width: 100%;
    padding: 20px;

    li {
      margin-bottom: 16px;
      font-size: 15px;
      color: $grey-8;
    }
  }
}

@media (max-width: 640px) {
  .search-results {
    padding-top: 0;
  }

  .sproduct-list {
    grid-template-columns: 1fr;
    grid-gap: 20px;

    .product-item {
      display: grid;
      grid-template-columns: 70px 1fr;
      grid-template-rows: 37px;
      grid-template-areas:
        "product-img product-title"
        "product-img product-price";
      column-gap: 40px;

      .product-img {
        grid-area: product-img;
      }
      .product-title {
        text-align: left;
        margin-top: 2px;
        word-wrap: nowrap;
        overflow: hidden;
        grid-area: product-title;
      }
      .product-price {
        text-align: left;
        margin-top: 2px;
        grid-area: product-price;
      }
    }
  }
}

@media (min-width: 1038px) {
