.mainpage > div {
  width: 100%; }

.navigation {
  position: absolute;
  z-index: 999;
  height: 106px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  color: #eeebe3;
  transition: background 0.5s ease-out, color 0.25s ease-out; }

.navigation div {
  flex: 0 1 auto; }

.left-nav {
  margin-left: 24px; }

.right-nav {
  margin-right: 24px; }

.right-nav li,
.left-nav li {
  font-size: 14px;
  margin: 0 14px;
  display: inline-block;
  flex-grow: 0;
  flex-basis: 240px; }

.mobile {
  display: none; }

.opaque-nav {
  background: #f1ebdd;
  color: #1d1d1d; }

.search-results {
  font-size: 16px; }
  .search-results .section-label {
    line-height: 2.5em;
    width: 100%;
    border-bottom: 1px solid #cfcac2;
    margin: 0 0 40px; }

.header-banner {
  width: 100%;
  height: 550px;
  justify-content: center;
  align-items: center; }
  .header-banner .img-wrapper {
    background: #1d1d1d;
    transform: translate3d(0px, 0px, 0px);
    height: 100%;
    width: 100%;
    /* Create the parallax scrolling effect */
    background-attachment: fixed;
    background-position: center center;
    background-repeat: no-repeat;
    background-size: cover;
    filter: contrast(0.4);
    -webkit-filter: contrast(0.4); }
  .header-banner .caption {
    position: absolute;
    margin: 0 auto; }

.content-main {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  margin-bottom: 60px; }
  .content-main .nav-toolbar {
    width: 100%;
    height: 60px;
    margin-bottom: 20px;
    border-top: 1px solid #cfcac2;
    border-bottom: 1px solid #cfcac2; }
    .content-main .nav-toolbar .nav-btn {
      display: flex;
      height: 100%;
      flex-basis: 160px;
      border-left: 1px solid #cfcac2; }
  .content-main .products {
    display: flex;
    font-size: 16px;
    width: 100%;
    padding: 20px; }
    .content-main .products .filter {
      padding-left: 20px;
      width: 180px; }
      .content-main .products .filter li {
        margin-bottom: 16px;
        font-size: 15px;
        color: #616161; }

.product-list {
  width: 100%;
  display: grid;
  grid-gap: 40px;
  grid-template-columns: repeat(4, minmax(240px, 1fr)); }
  .product-list .product-item .product-img {
    width: 100%;
    max-width: 100%;
    transition: transform 0.25s ease-out; }
  .product-list .product-item .product-img:hover {
    transform: translate(2px, 2px); }

.sproduct-list {
  width: 100%;
  display: grid;
  grid-gap: 40px;
  justify-content: space-evenly;
  grid-template-columns: repeat(4, minmax(110px, 380px)); }
  .sproduct-list .product-item .product-img {
    width: 100%;
    max-width: 100%;
    transition: transform 0.25s ease-out; }
  .sproduct-list .product-item .product-img:hover {
    transform: translate(2px, 2px); }
  .sproduct-list .product-item .product-title {
    overflow: hidden; }

.filter-drawer .filter-title {
  display: flex;
  align-items: center;
  justify-content: center;
  border-bottom: 1px solid #cfcac2; }
  .filter-drawer .filter-title > i {
    position: absolute;
    right: 20px; }

.filter-drawer .filter-selection {
  display: flex;
  font-size: 16px;
  width: 100%;
  padding: 20px; }
  .filter-drawer .filter-selection li {
    margin-bottom: 16px;
    font-size: 15px;
    color: #616161; }

@media (max-width: 640px) {
  .search-results {
    padding-top: 0; }
  .sproduct-list {
    grid-template-columns: 1fr;
    grid-gap: 20px; }
    .sproduct-list .product-item {
      display: grid;
      grid-template-columns: 70px 1fr;
      grid-template-rows: 37px;
      grid-template-areas: "product-img product-title" "product-img product-price";
      column-gap: 40px; }
      .sproduct-list .product-item .product-img {
        grid-area: product-img; }
      .sproduct-list .product-item .product-title {
        text-align: left;
        margin-top: 2px;
        word-wrap: nowrap;
        overflow: hidden;
        grid-area: product-title; }
      .sproduct-list .product-item .product-price {
        text-align: left;
        margin-top: 2px;
        grid-area: product-price; } }

@media (min-width: 1038px) {
  .filter-btn {
    display: none !important; } }

@media (max-width: 1038px) {
  .filter {
    display: none !important; } }

