.home > div {
  width: 100%;
}

.navigation {
  position: absolute;
  z-index: 999;
  height: 106px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  color: $link;
  transition: background 0.5s ease-out, color 0.25s ease-out;
}
.navigation div {
  flex: 0 1 auto;
}
.left-nav {
  margin-left: 24px;
}
.right-nav {
  margin-right: 24px;
}
.right-nav li,
.left-nav li {
  font-size: 14px;
  margin: 0 14px;
  display: inline-block;
  flex-grow: 0;
  flex-basis: 240px;
}
.mobile {
  display: none;
}
.custom-caption {
  padding: 70px;
  color: white;
}
.opaque-nav {
  background: $navbg;
  color: $dark;
}
.search-results {
  font-size: 16px;

  .section-label {
    line-height: 2.5em;
    width: 100%;
    border-bottom: 1px solid $line;
    margin: 0 0 40px;
  }
}
.newitems {
  padding: 80px 0;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;

  .content {
    font-size: 16px;
    width: 100%;
    padding: 20px;
  }
}
.scroll-btn {
  position: absolute;
  left: 0;
  right: 0;
  margin: -21px auto;
  transition: transform 0.25s ease-out;
}
.scroll-btn:hover {
  transform: scale(1.25);
}
// NEW ITEMS
.product-list {
  margin-left: 40px;
  margin-right: 40px;
  display: grid;
  grid-gap: 40px;
  grid-template-columns: repeat(4, minmax(300px, 1fr));

  .product-item {
    .product-img {
      width: 100%;
      max-width: 100%;
      transition: transform 0.25s ease-out;
    }

    .product-img:hover {
      transform: translate(2px, 2px);
    }
  }
}

// SEARCH RESULTS
.sproduct-list {
  width: 100%;
  display: grid;
  grid-gap: 40px;
  justify-content: space-evenly;
  grid-template-columns: repeat(4, minmax(110px, 380px));

  .product-item {
    .product-img {
      width: 100%;
      max-width: 100%;
      transition: transform 0.25s ease-out;
    }

    .product-img:hover {
      transform: translate(2px, 2px);
    }
    .product-title {
      overflow: hidden;
    }
  }
}

@media (max-width: 640px) {
  .search-results {
    padding-top: 0;
  }

  .sproduct-list {
    grid-template-columns: 1fr;
    grid-gap: 20px;

    .product-item {
      display: grid;
      grid-template-columns: 70px 1fr;
      grid-template-rows: 37px;
      grid-template-areas:
        "product-img product-title"
        "product-img product-price";
      column-gap: 40px;

      .product-img {
        grid-area: product-img;
      }
      .product-title {
        text-align: left;
        margin-top: 2px;
        word-wrap: nowrap;
        overflow: hidden;
        grid-area: product-title;
      }
      .product-price {
        text-align: left;
        margin-top: 2px;
        grid-area: product-price;
      }
    }
  }
}

@media (max-width: 1390px) {
  .product-list {
    margin-left: 40px;
    margin-right: 40px;
    grid-template-columns: repeat(2, minmax(300px, 1fr));
  }
}
@media (max-width: 1220px) {
  .left-nav {
    margin-left: 8px;
  }
  .right-nav {
    margin-right: 8px;
  }
  .left-nav li,
  .right-nav li {
    margin: 0;
  }
  .mobile {
    display: inline-block;
  }
  .default {
    display: none;
  }
  .center-nav {
    position: relative !important;
    top: 4px !important;
    left: 4px !important;
  }
}
@media (max-width: 760px) {
  .product-list {
    margin-left: 40px;
    margin-right: 40px;
    justify-content: center;
    grid-template-columns: 0.8fr;
  }
}
