.mainpage > div {
  width: 100%;
}
.navigation {
  height: 106px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  color: $link;
  transition: background 0.5s ease-out, color 0.25s ease-out;
}
.navigation div {
  flex: 0 1 auto;
}
.left-nav {
  margin-left: 24px;
}
.right-nav {
  margin-right: 24px;
}
.right-nav li,
.left-nav li {
  font-size: 14px;
  margin: 0 14px;
  display: inline-block;
  flex-grow: 0;
  flex-basis: 240px;
}
.opaque-nav {
  box-shadow: 0 0.1rem #eecfc8;
  background: $navbg;
  color: $dark;
}
.grid {
  display: grid;
  padding: 50px;
  grid-template-columns: repeat(3, minmax(320px, 400px));
  grid-template-areas:
    "gallery gallery oform"
    "info info oform";
  grid-gap: 50px;
  justify-content: center;

  .gallery {
    grid-area: gallery;
    min-height: 480px;
  }
  .info {
    grid-area: info;
    border-top: 1px solid rgba(0, 0, 0, 0.12);

    .detail li span {
      font-family: "Source Sans Pro" !important;
    }
  }
  .oform {
    grid-area: oform;
  }
}
.gallery {
  .prev-action {
    position: absolute;
    top: 50%;
    left: 4px;
    margin-top: -16px;
    cursor: pointer;
  }
  .next-action {
    position: absolute;
    top: 50%;
    right: 4px;
    margin-top: -16px;
    cursor: pointer;
  }
  .thumbnails .thumb-selected {
    border: 1px solid $primary;
  }
}
.oform {
  .description {
    font-family: "Source Sans Pro" !important;
    text-align: justify;
  }
}
.comments,
.related {
  padding: 50px 0;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  border-top: 1px solid rgba(0, 0, 0, 0.12);

  .content {
    font-size: 16px;
    width: 100%;
    padding: 20px;
  }
}
.comments .content {
  display: flex;
  align-items: flex-start;
  width: 100%;
  padding: 0 40px;

  .comments-list {
    width: 50%;

    * {
      font-family: "Source Sans Pro" !important;
      line-height: 1.5em !important;
    }

    .level-1 {
      max-width: 720px;
    }
    .level-2 {
      max-width: 720px;
      padding-left: 60px;
    }
    .author {
      font-size: 16px;
      font-weight: 700;
    }
  }

  .comment-form {
    width: 50%;
    max-width: 720px;
  }
}

.product-list {
  margin-left: 40px;
  margin-right: 40px;
  display: grid;
  grid-gap: 40px;
  grid-template-columns: repeat(4, minmax(300px, 1fr));

  .product-item {
    .product-img {
      width: 100%;
      max-width: 100%;
      transition: transform 0.25s ease-out;
    }

    .product-img:hover {
      transform: translate(2px, 2px);
    }
  }
}

@media (max-width: 1390px) {
  .product-list {
    margin-left: 40px;
    margin-right: 40px;
    grid-template-columns: repeat(2, minmax(300px, 1fr));
  }
}
@media (max-width: 1110px) {
  .grid {
    grid-template-columns: 1fr;
    grid-template-areas:
      "gallery"
      "oform"
      "info";
  }
  .comments .content {
    flex-direction: row;
    flex-wrap: wrap-reverse;
    justify-content: center;

    .comments-list,
    .comment-form {
