.mainpage > div {
  width: 100%;
}

.checkout {
  min-height: 80vh;
  font-size: 16px;
  max-width: 1200px;
  margin: 60px auto;
  padding: 0 40px;
  gap: 40px;

  & > div {
    width: 100%;
  }

  .checkout-heading {
    flex: 0 0 560px;
    padding-right: 40px;
    border-right: 1px solid $line;
  }

  .field-heading {
    line-height: 2em;
  }

  .cart-wrapper {
    flex: 0 0 480px;
    margin-top: 10px;
  }

  .cart {
    width: 100%;
    display: table;
    table-layout: auto;
    border-collapse: collapse;
  }

  .cart-item {
    display: table-row;

    .product-info {
      display: table-cell;
      vertical-align: middle;
      padding: 10px 16px 20px 0;
    }

    .product-price {
      width: 120px;
    }
    .product-img {
      width: 100px;
    }

    .product-title {
      max-width: 260px;
    }
  }

  .bordered {
    border-top: 1px solid $line;
  }
}

.overflow-ellipsis {
  display: block;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

@media (max-width: 1182px) {
  .checkout {
    padding: 0 20px;
    flex-direction: column-reverse;
  }
  .checkout-heading,
  .cart-wrapper {
    margin: 0 auto;
    max-width: 720px;
    padding-right: 0 !important;
    border-right: none !important;
    flex: 0 1 auto !important;
  }
}
