export default {
  name: "AccountLayout",
  preFetch({ store, redirect }) {
    return store.dispatch("home/getPSA").catch(err => {
      redirect("/error500");
    });
  },
  computed: {
    psa() {
      let psa = this.$store.state.home.psa;
      if (psa)
        return {
          message: this.$sanitize(psa.message),
          link:
            psa.targetLink.indexOf("https") !== -1
              ? psa.targetLink
              : "https://" + psa.targetLink
        };
      return null;
    }
  },
  meta: {
    title: "Account",
    titleTemplate: title => `${title} | Bake Free Web Front`,
    // meta tags
    meta: {
      description: {
        name: "description",
        content: "Web Store Front for Bake Free"
      },
      keywords: { name: "keywords", content: "web store front bake free" },
      equiv: {
        "http-equiv": "Content-Type",
        content: "text/html; charset=UTF-8"
      }
    }
  },
  mounted() {
    // Deferred footer layout
    this.showFooter = true;
  },
  data() {
    return {
      showFooter: false,
      leftDrawerOpen: false
    };
  }
};
