import HelperMixin from "../../mixins/helpers";
import Navigation from "../../components/Navigation";

export default {
  name: "Account",
  mixins: [HelperMixin],
  components: { Navigation },

  preFetch({ store, redirect }) {
    if (store.getters["auth/isAuthenticated"]) {
      redirect("/profile");
    }
  },

  meta() {
    return {
      title: "Account"
    };
  },
  created() {},
  mounted() {},
  data() {
    return {
      loginErr: "",
      loading: false,
      rloading: false,
      login: {
        email: "",
        password: ""
      },
      registerErr: "",
      register: {
        email: "",
        password: "",
        firstname: "",
        lastname: "",
        reseller: false
      }
    };
  },
  methods: {
    _isValidEmail(val) {
      const emailPattern = /^(?=[a-zA-Z0-9@._%+-]{6,254}$)[a-zA-Z0-9._%+-]{1,64}@(?:[a-zA-Z0-9-]{1,63}\.){1,8}[a-zA-Z]{2,63}$/;
      return emailPattern.test(val) || "Invalid email format";
    },

    async onLogin() {
      this.loginErr = "";
      this.loading = true;
      try {
        await this.$store.dispatch("auth/signin", { ...this.login });
        if (this.$route.query.ref) {
          this.$router.push(this.$route.query.ref).catch(err => {});
        } else {
          this.$router.push("/").catch(err => {});
        }
      } catch (err) {
        this.loginErr = err;
      } finally {
        this.loading = false;
      }
    },

    async onRegister() {
      this.registerErr = "";
      this.rloading = true;
      try {
        await this.$store.dispatch("auth/register", { ...this.register });
        this.showNotif(
          "info",
          "Please verify your account before completing your purchase.",
          5000
        );
        this.$router.push("/").catch(err => {});
      } catch (err) {
        this.registerErr = err;
      } finally {
        this.rloading = false;
      }
    }
  }
};

