        const res = await this.$store.dispatch("gallery/searchProducts", {
          search: this.search
        });
        if (!res) {
          this.searchRes = "No items found.";
        }
      } catch (err) {
        this.searchRes = err + "Error occurred while searching for products.";
      } finally {
        this.fetching = false;
      }
    }
  }
};

