import HelperMixin from "../mixins/helpers";
export default {
  name: "Navigation",
  mixins: [HelperMixin],
  props: {
    opaque: {
      type: Boolean,
      required: true,
      default: true
    }
  },
  computed: {
    sProducts() {
      return this.$store.getters["gallery/searchResults"];
    },
    itemsCount() {
      return this.$store.getters["basket/itemsCount"] || "0";
    }
  },
  data() {
    return {
      hover: this.opaque,
      showDlg: false,
      search: "",
      searchRes: "",
      fetching: false
    };
  },
  methods: {
    clearSearch() {
      this.searchRes = this.search = "";
    },
    showSearchDlg() {
      this.showDlg = true;
    },
    async searchProducts(val) {
      try {
        this.searchRes = "";
        this.fetching = true;
        const res = await this.$store.dispatch("gallery/searchProducts", {
          search: this.search
        });
        if (!res) {
          this.searchRes = "No items found.";
        }
      } catch (err) {
        this.searchRes = err + "Error occurred while searching for products.";
      } finally {
        this.fetching = false;
      }
    }
  }
};

